import { useEffect, useState } from 'react';
import './App.css';

function App() {
  const[quote,setquote]=useState("")
  const getQuote=()=>{
    // convert response to response.json ,converting api data to setquote,setquote has all data of api
    fetch("https://api.quotable.io/random").then((response)=>response.json()).then((data)=>setquote(data))
  }
  useEffect(()=>getQuote(),[])
  // empty dependency will work only as compdidmount 
  return (
    <div className="App">
      <span>{quote.content}</span>
      <h4>{quote.author}</h4>
      <button onClick={getquote}>Get Quote</button>
    </div>
  );
}

export default App;
